import React from "react";
import { Formik, yupToFormErrors } from "formik";
import * as Yup from 'yup';
import "./styles.css";
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";
import illustration from './PlexFigure.png';

import { createTheme, ThemeProvider } from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

//Theme Configuration
const theme = createTheme({
  status: {
    danger: '#e53e3e',
  },
  palette: {
    primary: {
      main: '#ff8a00',
    },
    neutral: {
      main: '#ffffff',
      contrastText: '#ff8a00',
    },
  },
});

//Role Selector Configuration
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const roles = [
  'Member',
  'Curriculum Instructor',
];

function getStyles(role, roles, theme) {
  return {
    fontWeight:
      roles.indexOf(role) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

function RoleSelector(props) {
  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    props.setRoles(
      typeof value === 'string' ? value.split(',') : value,
    );
  };

  return (
    <FormControl id='roleSelector' sx={{ m: 1, width: 300 }}>
      <label id="desiredRoles">Intended Roles</label>
      <Select
        labelId="roles"
        id="roles"
        multiple
        value={props.roles}
        onChange={handleChange}
        input={<OutlinedInput label="Role" />}
        MenuProps={MenuProps}
      >
        {roles.map((role) => (
          <MenuItem
            key={role}
            value={role}
            style={getStyles(role, roles, theme)}
          >
            {role}
          </MenuItem>
        ))}
      </Select>
    </FormControl>
  );
}


const ApplicationForm = () => {
  const navigate = useNavigate();
  const [role, setRole] = React.useState([]);
  let [resume, setResume] = React.useState(null);

  const navToHome = () => {
    navigate("/");
  }
  return (
    <>
    <div>
    <ThemeProvider theme={theme}>
        <Formik
          initialValues={{
            firstName: '',
            lastName: '',
            timestamp: Date.now(),
            desiredRoles: [],
            resume: '',
          }}
          validationSchema={
            Yup.object(
              {
                firstName: Yup.string().required('Required'),
                lastName: Yup.string().required('Required'),
                desiredRoles: Yup.array().required('Required'),
              }
            )
          }
          onSubmit={async (values) => {
            fetch('http://127.0.0.1:5000/add_applicant', {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({
                first_name: values.firstName,
                last_name: values.lastName,
                time_created: values.timestamp,
                desired_roles: values.desiredRoles,
                resume: values.resume,
              })
            });
            // values.desiredRoles = roles;
            // alert(JSON.stringify(values, null, 2));
          }}
        >
          {formik => (
            <div className='application-form'>
              <span>

                <Button style={{ "display": "flex" }}
                  variant="contained"
                  color="neutral"
                  onClick={navToHome}
                  className="navHome">Return Home</Button>

                <h1 style={{ "display": "flex" }}>PlexTech Application - Spring 2023</h1>

                <br />
              </span>

              <div>
                <h4>Thank you for your interest in PlexTech! Please fill out the information below and we will get back to you soon. </h4>

                <form onSubmit={formik.handleSubmit}>

                  {/* First Name */}
                  <label htmlFor="firstName">First Name</label>
                  <input
                    id="firstName"
                    type="text"
                    {...formik.getFieldProps('firstName')} />
                  {formik.touched.firstName && formik.errors.firstName ? <div>{formik.errors.firstName}</div> : null}

                  {/* Last Name */}
                  <label htmlFor="lastName">Last Name</label>
                  <input
                    id="lastName"
                    type="text"
                    {...formik.getFieldProps('lastName')} />
                  {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}

                  {/* Desired Roles */}
                  <div>
                    <RoleSelector id='desiredRoles' roles={role} setRoles={setRole} />
                  </div>
                  {formik.errors.desiredRoles ? <div>{formik.errors.desiredRoles}</div> : null}

                  {/* Resume Upload */}
                  <label htmlFor="resume">Resume / CV</label>
                  <input
                    id='resume'
                    type='file'
                    {...formik.getFieldProps('resume')} />
                  {formik.touched.resume && formik.errors.resume ? <div>{formik.errors.resume}</div> : null}

                  {/* Timestamp */}
                  <label htmlFor="timestamp">Timestamp</label>
                  <input
                    id="timestamp"
                    name="timestamp"
                    type="text"
                    readOnly
                    value={formik.values.timestamp} />

                  {/* Submit Button */}
                  <div>
                    <Button type="submit"
                      variant="contained"
                      color="neutral"
                      fontWeight="Bold"
                      style={{ "marginBottom": "50px" }}
                    >Submit</Button>
                  </div>
                  <p className="copyright">Copyright Â© 2022 PlexTech All Rights Reserved.</p>
                </form>
              </div>
            </div>
          )}
        </Formik>
      </ThemeProvider>
      <img src={illustration} className='image'></img>
    </div>
    </>
  );
};

export default ApplicationForm;
