import React from "react";
import { Formik } from "formik";
import * as Yup from 'yup';
import "./styles.css";
import Button from '@mui/material/Button';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from "react-router-dom";

const theme = createTheme({
  status: {
    danger: '#e53e3e',
  },
  palette: {
    primary: {
      main: '#ff8a00',
    //   darker: '#053e85',
    },
    neutral: {
      main: '#ffffff',
       contrastText: '#ff8a00',
    },
  },
});

const InvoiceForm = () => {
    const navigate = useNavigate();

    const navToHome = () => {
      navigate("/");
  }
      return (
        <ThemeProvider theme={theme}>
          <Formik
              initialValues = {{
                  firstName : '',
                  lastName : '',
                  timestamp: Date.now(),
              }}
              validationSchema = {
                  Yup.object(
                      {
                          firstName: Yup.string().required('Required'),
                          lastName: Yup.string().required('Required'),
                      }
                  )
              }
              onSubmit={ async (values) => {
                  fetch('<ENTER API ENDPOINT HERE>', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                      first_name: values.firstName,
                      last_name: values.lastName,
                      time_created: values.timestamp,
                     })
                  });
              }}
          >
              {formik => (
                    <div>
                      <span>
                        <Button style={{"display": "flex"}}
                        variant = "contained"
                        color = "neutral"
                        onClick={navToHome} 
                        className="navHome"
                        >Return Home</Button>
                        <h1 style={{"display": "flex"}}>
                          PlexTech Member Application - Spring 2023
                        </h1>
                        <br/>
                      </span>    
                      <div>
                        <h4>
                          Thank you for your interest in PlexTech! Please fill out the information below and we will get back to you as soon as possible.
                        </h4>
                        <form onSubmit={formik.handleSubmit}>
                        <label htmlFor="firstName">First Name</label>
                        <input
                          id="firstName"
                          type="text"
                          {...formik.getFieldProps('firstName')}
                        />
                        {formik.touched.firstName && formik.errors.firstName ? <div>{formik.errors.firstName}</div> : null}

                        <label htmlFor="lastName">Last Name</label>
                        <input
                          id="lastName"
                          type="text"
                          {...formik.getFieldProps('lastName')}
                        />
                        {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}

                        <label htmlFor="timestamp">Timestamp</label>
                        <input
                          id="timestamp"
                          name="timestamp"
                          type="text"
                          readOnly
                          value={formik.values.timestamp}
                        />

                        <div>
                          <Button type="submit"
                          variant = "contained"
                          color = "neutral"
                          fontweight = "Bold"
                          >Submit</Button>
                        </div>
                      </form>
                      </div>  
                    </div>    
              )}
          </Formik>
        </ThemeProvider>
      );
  };

  export default InvoiceForm;



