import React, { useState, useEffect } from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import "./styles.css";
import Button from "@mui/material/Button";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate, useLocation } from "react-router-dom";

function AdminConsole() {

    const { state } = useLocation();
    const navigate = useNavigate();
    const [graderMessage, setGraderMessage] = useState("");
    const [graders, setGraders] = useState();

    const theme = createTheme({
        status: {
            danger: "#e53e3e",
        },
        palette: {
            primary: {
                main: "#ff8a00",
            },
            neutral: {
                main: "#ffffff",
                contrastText: "#ff8a00",
            },
        },
    });

    useEffect(() => {
        async function checkUser(value) {
            await fetch('http://127.0.0.1:5000/check_admin', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    email: value.email,
                })
            })
                .then((response) => {
                    return (response.json());
                })
                .then((data) => {
                    if (data.found === false) {
                        navigate('/admin-login');
                    }
                });
        };

        if (state === null) {
            navigate('/admin-login');
        } else {
            checkUser(state);
        }

        async function fetchGraderData() {
            await fetch("http://127.0.0.1:5000/get_graders", {
                method: 'GET',
            })
                .then((response) => {
                    return (response.json());
                })
                .then((data) => {
                    setGraders(data);
                })
                .catch((err) => {
                    console.log(err.message);
                });
        }
        fetchGraderData();
    }, []);

    return (
        <>
            <ThemeProvider theme={theme}>
                <span>
                    <Button
                        style={{ display: "flex" }}
                        variant="contained"
                        color="neutral"
                        onClick={() => { navigate('/') }}
                        className="navHome"
                    >Return Home</Button>

                    <h1 style={{ display: "flex" }}>
                        PlexTech Administrator Console
                    </h1>
                </span>

                {/* Grader Control */}
                <Formik
                    initialValues={{
                        email: "",
                    }}
                    onSubmit={async (values) => {
                        if (values.action === "add") {
                            await fetch('http://127.0.0.1:5000/add_grader', {
                                method: 'POST',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify({
                                    email: values.email,
                                })
                            }).then(() => {
                                setGraderMessage("Successfully added grader " + values.email + ".")
                            });
                        } else if (values.action === "remove") {
                            await fetch('http://127.0.0.1:5000/remove_grader', {
                                method: 'DELETE',
                                headers: { 'Content-Type': 'application/json' },
                                body: JSON.stringify({
                                    email: values.email,
                                })
                            }).then(() => {
                                setGraderMessage("Successfully removed grader " + values.email + ".")
                            });
                        }
                    }}
                >
                    {formik => (
                        <div className='admin-console'>
                            <span>
                                <Button style={{ "display": "flex" }}
                                    variant="contained"
                                    color="neutral"
                                    onClick={navToHome}
                                    className="navHome">Return Home</Button>

                                <h1 style={{ "display": "flex" }}>PlexTech Application - Spring 2023</h1>

                                <br />
                            </span>

                            <div>

                                <p>{graders}</p>

                                <form onSubmit={formik.handleSubmit}>

                                    {/* Last Name */}
                                    <label htmlFor="lastName">Last Name</label>
                                    <input
                                        id="lastName"
                                        type="text"
                                        {...formik.getFieldProps('lastName')} />
                                    {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}

                                    <label htmlFor="rating1">
                                        What do you want to do with this grader?
                                    </label>
                                    <select className="dropbtn" name="action1" value={action1} onChange={(event) => { setRating1(event.target.value) }}>
                                        <option value="" disabled={true}>Choose a rating:</option>
                                        <option value={"add"}>add grader</option>
                                        <option value={"remove"}>remove grader</option>
                                    </select>

                                    {/* Resume Upload */}
                                    <label htmlFor="resume">Resume / CV</label>
                                    <input
                                        type='file'
                                        accept='application/pdf'
                                        onChange={saveResume}
                                    />

                                    {/* Long Answer 1 */}
                                    <label htmlFor="answer1">There are many technology organization on campus;<br />what inspires you to join PlexTech?</label>
                                    <p>(~300 words)</p>
                                    <input
                                        id='answer1'
                                        type='text'
                                        {...formik.getFieldProps('answer1')} />
                                    {formik.touched.answer1 && formik.errors.answer1 ? <div>{formik.errors.answer1}</div> : null}

                                    {/* Long Answer 2 */}
                                    <label htmlFor="answer1">Tell us a story that best captures you as a person.</label>
                                    <p>(~300 words)</p>
                                    <input
                                        id='answer2'
                                        type='text'
                                        {...formik.getFieldProps('answer2')} />
                                    {formik.touched.answer2 && formik.errors.answer2 ? <div>{formik.errors.answer2}</div> : null}

                                    {/* EC Commitments */}
                                    <label htmlFor="commitments">Please tell us about your commitments.</label>
                                    <p>(Example: CS61A: xx hours)</p>
                                    <input
                                        id='commitments'
                                        type='text'
                                        {...formik.getFieldProps('commitments')} />
                                    {formik.touched.commitments && formik.errors.commitments ? <div>{formik.errors.commitments}</div> : null}

                                    {/* Timestamp */}
                                    <label htmlFor="timestamp">Timestamp</label>
                                    <p>Auto-generated for application identification only;<br />this will not affect your application.</p>
                                    <input
                                        id="timestamp"
                                        name="timestamp"
                                        type="text"
                                        readOnly
                                        value={formik.values.timestamp} />

                                    {/* Submit Button */}
                                    <div>
                                        <Button type="submit"
                                            variant="contained"
                                            color="neutral"
                                            fontWeight="Bold"
                                            style={{ "marginBottom": "50px" }}
                                        >Submit</Button>
                                    </div>
                                    <p className="copyright">Copyright Â© 2022 PlexTech All Rights Reserved.</p>
                                </form>
                            </div>
                        </div>
                    )}
                </Formik>
            </ThemeProvider>
        </>
    )
}

export default AdminConsole;
