import React, { useState, useEffect } from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import "./styles.css";
import Button from "@mui/material/Button";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate } from "react-router-dom";

const theme = createTheme({
  status: {
    danger: "#e53e3e",
  },
  palette: {
    primary: {
      main: "#ff8a00",
    },
    neutral: {
      main: "#ffffff",
      contrastText: "#ff8a00",
    },
  },
});

const InvoiceForm = () => {
  const navigate = useNavigate();
  const [userInfo, setData] = useState({});
  const [rating1, setRating1] = useState(0);
  const [rating2, setRating2] = useState(0);
  const [rating3, setRating3] = useState(0);

  const navToHome = () => {
    navigate("/");
  };

  useEffect(() => {
    async function fetchData() {
      await fetch("http://127.0.0.1:5000/get_applicant", {
        method: 'GET',
      })
        .then((response) => {
          return (response.json());
        })
        .then((data) => {
          data = data[0]
          setData({
            timestamp: data.time_created,
            firstName: data.first_name,
            lastName: data.last_name,
            resume: data.resume,
            answer1: data.answer1,
            answer2: data.answer2,
            commitments: data.commitments,
          });
        })
        .catch((err) => {
          console.log(err.message);
        });
    }

    fetchData();

  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Formik
        initialValues={{
          timestamp: userInfo.timestamp,
          firstName: userInfo.firstName,
          lastName: userInfo.lastName,
          resume: userInfo.resume,
          answer1: userInfo.answer1,
          answer2: userInfo.answer2,
          commitments: userInfo.commitments,
        }}
        onSubmit={async (values) => {
          await fetch('http://127.0.0.1:5000/add_review', {
            method: "POST",
            headers: { "Content-Type": "grader/json" },
            body: JSON.stringify({
              // add input values here: for format, refer back to the application form
              first_name: values.firstName,
              last_name: values.lastName,
              grader_name: 'placeholder',
              comment1: values.comment1,
              rating1: rating1,
              comemnt2: values.comment2,
              rating2: rating2,
              comment3: values.comment3,
              rating3: rating3,
            }),
          });
        }}
      >
        {(formik) => (
          <div>
            <span>
              <Button
                style={{ display: "flex" }}
                variant="contained"
                color="neutral"
                onClick={navToHome}
                className="navHome"
              >Return Home</Button>

              <h1 style={{ display: "flex" }}>
                PlexTech Grader Portal - Spring 2023
              </h1>
            </span>

            <div>
              <h4>
                For each applicant, please provide a rating and leave thorough
                comments for every essay question.
              </h4>

              <form onSubmit={formik.handleSubmit}>

                <label htmlFor="timestamp">Timestamp</label>
                <div>
                  <p>{typeof userInfo.timestamp != typeof undefined ? userInfo.timestamp : 'N/A'}</p>
                </div>

                <label htmlFor="firstName">First Name</label>
                <div>
                  <p>{userInfo.firstName}</p>
                </div>

                <label htmlFor="lastName">Last Name</label>
                <div>
                  <p>{userInfo.lastName}</p>
                </div>

                <label htmlFor="resume">Resume/CV</label>

                {/* Show resume preview here */}
                <p>Placeholder; figure out how to show the preview.</p>

                <div className="horizontal-box">
                  <div>
                    <label htmlFor="answer1">
                      Long Answer: Why PlexTech?
                    </label>
                    <p>{userInfo.answer1}</p>
                  </div>

                  <div>
                    <label htmlFor="comment1">Comment</label>

                    <div>
                      <input
                        className="commentHeight"
                        id="comment1"
                        type="text"
                        {...formik.getFieldProps("comment1")}
                      />

                      <div>
                        <label htmlFor="rating1">
                          Choose a rating for this essay question:
                        </label>
                        <select className="dropbtn" name="rating1" id="rating1" onChange={(value) => {setRating1(value)}}>
                          <option value="one">1</option>
                          <option value="two">2</option>
                          <option value="three">3</option>
                          <option value="four">4</option>
                          <option value="five">5</option>
                        </select>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="horizontal-box">
                  <div>
                    <label htmlFor="essay2">
                      Long Answer: A Story about Yourself
                    </label>

                    <input
                      className="bigText"
                      id="essay2"
                      name="essay2"
                      type="text"
                      readOnly
                      value={formik.values.essay2}
                    />
                  </div>

                  <div>
                    <label htmlFor="comment2">Comment</label>
                    <input
                      className="commentHeight"
                      id="comment2"
                      type="text"
                      {...formik.getFieldProps("comment2")}
                    />

                    <div>
                      <label htmlFor="rating2">
                        Choose a rating for this essay question:
                      </label>
                      <select className="dropbtn" name="rating2" id="rating2" onChange={(value) => {setRating2(value)}}>
                        <option value="one">1</option>
                        <option value="two">2</option>
                        <option value="three">3</option>
                        <option value="four">4</option>
                        <option value="five">5</option>
                      </select>
                    </div>
                  </div>
                </div>

                <div className="horizontal-box">
                  <div>
                    <label htmlFor="commitments">
                      Long Answer: Why PlexTech?
                    </label>
                    <p>{userInfo.commitments}</p>
                  </div>

                  <div>
                    <label htmlFor="comment3">Comment</label>

                    <div>
                      <input
                        className="commentHeight"
                        id="comment1"
                        type="text"
                        {...formik.getFieldProps("comment3")}
                      />

                      <div>
                        <label htmlFor="rating3">
                          Choose a rating for this essay question:
                        </label>
                        <select className="dropbtn" name="rating3" id="rating3" onChange={(value) => {setRating3(value)}}>
                          <option value="one">1</option>
                          <option value="two">2</option>
                          <option value="three">3</option>
                          <option value="four">4</option>
                          <option value="five">5</option>
                        </select>
                      </div>
                    </div>
                  </div>
                </div>

                <div>
                  <Button
                    type="submit"
                    variant="contained"
                    color="neutral"
                    fontWeight="Bold"
                    style={{ marginBottom: "4rem", marginTop: "3rem" }}
                  >
                    Submit
                  </Button>
                </div>
              </form>
            </div>
          </div>
        )}
      </Formik>
    </ThemeProvider>
  );
};

export default InvoiceForm;
