import React, { useState, useEffect, useRef } from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import "./styles.css";
import Button from "@mui/material/Button";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate, useLocation } from "react-router-dom";
import LinearProgress from '@mui/material/LinearProgress';

const theme = createTheme({
  status: {
    danger: "#e53e3e",
  },
  palette: {
    primary: {
      main: "#ff8a00",
    },
    neutral: {
      main: "#ffffff",
      contrastText: "#ff8a00",
    },
  },
});

const GraderForm = () => {
  const navigate = useNavigate();
  const [userInfo, setData] = useState([{
    timestamp: "Loading applicants, please stand by...",
    firstName: "",
    lastName: "",
    resume: "",
    role: "",
    answer1: "",
    answer2: "",
    r0s: "",
    year: "",
    major: "",
    gender: "",
    linkedin: "N/A",
    website: "N/A",
    race: "",
  }]);

  const [r1, setR1] = useState("1");
  const [r2, setR2] = useState("1");
  const [r3, setR3] = useState("1");

  const [r4, setR4] = useState("1");
  const [r5, setR5] = useState("1");
  const [r6, setR6] = useState("1");
  const [r7, setR7] = useState("1");
  const [r8, setR8] = useState("1");
  const [r9, setR9] = useState("1");
  const [r0, setr0] = useState("1");

  const [comment0, setComment0] = useState('');
  const [comment1, setComment1] = useState('');
  const [comment2, setComment2] = useState('');
  const [comment3, setComment3] = useState('');
  const [comment4, setComment4] = useState('');
  const [loadingMessage, setLoading] = useState('');
  const [essaySubmitted, setEssaySubmitted] = useState(false);
  const resumeRef = useRef(null);
  const [completed, setCompleted] = useState(0);
  const [total, setTotal] = useState(0);

  const navToHome = () => {
    navigate("/");
  };

  useEffect(
    () => {
      if (essaySubmitted) {
        resumeRef.current?.scrollIntoView({ behavior: 'smooth' });
      }
    }, [essaySubmitted]
  );

  useEffect(
    () => {
      setLoading("");
    }, [completed]
  )

  const { state } = useLocation();

  //Load Applicant Data
  async function fetchData() {
    let url = `${process.env.REACT_APP_API_URL}/get_applicant/` + state.email
    await fetch(url, {
      method: 'GET',
    })
      .then((response) => {
        return (response.json());
      })
      .then((data) => {

        function base64ToArrayBuffer(data) {
          var binaryString = window.atob(data);
          var binaryLen = binaryString.length;
          var byteArray = new Uint8Array(binaryLen);
          for (var i = 0; i < binaryLen; i++) {
            var ascii = binaryString.charCodeAt(i);
            byteArray[i] = ascii;
          }
          return byteArray;
        };

        const applicantData = []
        if (data.length === 0) {
          navigate('/complete')
        }

        setTotal(data.length);

        for (let applicant of data) {
          var arrayBuffer = base64ToArrayBuffer(applicant.resume.slice(28));
          const blob = new Blob([arrayBuffer], { type: 'application/pdf' });
          const resumeURL = URL.createObjectURL(blob);

          applicantData.push({
            timestamp: applicant.time_created,
            firstName: applicant.first_name,
            lastName: applicant.last_name,
            resume: resumeURL,
            roles: applicant.desired_roles,
            answer1: applicant.answer1,
            answer2: applicant.answer2,
            answer3: applicant.answer3,
            commitments: applicant.commitments,
            year: applicant.year,
            major: applicant.major,
            gender: applicant.gender,
            race: applicant.race,
            linkedin: applicant.linkedin,
            website: applicant.website,
          })
        }
        setData(applicantData);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }

  //Mounting Calls
  useEffect(() => {

    //Authentication Check
    async function checkUser(value) {
      await fetch(`${process.env.REACT_APP_API_URL}/check_grader`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: value.email,
        })
      })
        .then((response) => {
          return (response.json());
        })
        .then((data) => {
          if (data.found === false) {
            navigate('/member-login');
          }
        });
    };

    if (state === null) {
      navigate('/member-login');
    } else {
      checkUser(state);
    }

    fetchData();
    setLoading('');
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <div className='form-field'>
        <Formik
          initialValues={{
            timestamp: userInfo[0].timestamp,
            firstName: userInfo[0].firstName,
            lastName: userInfo[0].lastName,
            resume: userInfo[0].resumeURL,
            roles: userInfo[0].roles,
            answer1: userInfo[0].answer1,
            answer2: userInfo[0].answer2,
            answer3: userInfo[0].answer3,
            r0s: userInfo[0].r0s,
            major: userInfo[0].major,
            year: userInfo[0].year,
            gender: userInfo[0].gender,
          }}
          onSubmit={async () => {
            setLoading('Submitting your review; please wait...');
            await fetch(`${process.env.REACT_APP_API_URL}/add_review`, {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({

                grader: state.email,
                comment0: comment0,
                r1: r1,
                r2: r2,
                r3: r3,
                comment1: comment1,
                r4: r4,
                r5: r5,
                comemnt2: comment2,
                r6: r6,
                r7: r7,
                comment3: comment3,
                r8: r8,
                r9: r9,
                comment4: comment4,
                r0: r0,
                applicantID: userInfo[0].timestamp,
              }),
            })
              .then(() => {
                let newData = userInfo;
                newData.shift();
                if (newData.length === 0) {
                  navigate('/complete')
                }
                setData(newData);
                window.scrollTo(0, 0);

                setR1('1');
                setR2('1');
                setR3('1');
                setR4('1');
                setR5('1');
                setR6('1');
                setR7('1');
                setR8('1');
                setR9('1');
                setr0('1');
                setComment0('');
                setComment1('');
                setComment2('');
                setComment3('');
                setComment4('');
              });
            setCompleted(completed + 1);
          }}
        >
          {(formik) => (
            <div>
              <span>
                <Button
                  style={{ display: "flex" }}
                  variant="contained"
                  color="neutral"
                  onClick={navToHome}
                  className="navHome"
                >Return Home</Button>

                <h1 style={{ display: "flex" }}>
