import React, { useState, useEffect } from "react";
import { Formik } from "formik";
import * as Yup from "yup";
import "./styles.css";
import Button from "@mui/material/Button";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate, useLocation } from "react-router-dom";

const theme = createTheme({
  status: {
    danger: "#e53e3e",
  },
  palette: {
    primary: {
      main: "#ff8a00",
    },
    neutral: {
      main: "#ffffff",
      contrastText: "#ff8a00",
    },
  },
});

const GraderForm = () => {
  const navigate = useNavigate();
  const [userInfo, setData] = useState([]);
  const [rating0, setRating0] = useState("1");
  const [rating1, setRating1] = useState("1");
  const [rating2, setRating2] = useState("1");
  const [rating3, setRating3] = useState("1");
  const [comment0, setComment0] = useState('');
  const [comment1, setComment1] = useState('');
  const [comment2, setComment2] = useState('');
  const [comment3, setComment3] = useState('');

  const navToHome = () => {
    navigate("/");
  };

  const { state } = useLocation();

  //Load Applicant Data
  async function fetchData() {
    let url = "http://127.0.0.1:5000/get_applicant/" + state.email
    await fetch(url, {
      method: 'GET',
    })
      .then((response) => {
        return (response.json());
      })
      .then((data) => {

        function base64ToArrayBuffer(data) {
          var binaryString = window.atob(data);
          var binaryLen = binaryString.length;
          var byteArray = new Uint8Array(binaryLen);
          for (var i = 0; i < binaryLen; i++) {
            var ascii = binaryString.charCodeAt(i);
            byteArray[i] = ascii;
          }
          return byteArray;
        };

        for (applicant in data) {
          var arrayBuffer = base64ToArrayBuffer(data.resume.slice(28));
          const blob = new Blob([arrayBuffer], { type: 'application/pdf' });
          const resumeURL = URL.createObjectURL(blob);

          setData(...userInfo, {
            timestamp: data.time_created,
            firstName: data.first_name,
            lastName: data.last_name,
            resume: resumeURL,
            answer1: data.answer1,
            answer2: data.answer2,
            commitments: data.commitments,
            year: data.year,
            major: data.major,
            gender: data.gender,
          });
        }

      })
      .catch((err) => {
        console.log(err.message);
      });
  }

  //Mounting Calls
  useEffect(() => {

    //Authentication Check
    async function checkUser(value) {
      await fetch('http://127.0.0.1:5000/check_grader', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: value.email,
        })
      })
        .then((response) => {
          return (response.json());
        })
        .then((data) => {
          if (data.found === false) {
            navigate('/member-login');
          }
        });
    };

    if (state === null) {
      navigate('/member-login');
    } else {
      checkUser(state);
    }

    fetchData();
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Formik
        initialValues={{
          timestamp: userInfo.timestamp,
          firstName: userInfo.firstName,
          lastName: userInfo.lastName,
          resume: userInfo.resumeURL,
          answer1: userInfo.answer1,
          answer2: userInfo.answer2,
          commitments: userInfo.commitments,
          major: userInfo.major,
          year: userInfo.year,
          gender: userInfo.gender,
        }}
        onSubmit={async () => {
          await fetch('http://127.0.0.1:5000/add_review', {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              first_name: userInfo.firstName,
              last_name: userInfo.lastName,
              grader_name: 'placeholder',
              comment0: comment0,
              rating0: rating0,
              comment1: comment1,
              rating1: rating1,
              comemnt2: comment2,
              rating2: rating2,
              comment3: comment3,
              rating3: rating3,
            }),
          })
            .then(() => navigate('/complete'));
        }}
      >
        {(formik) => (
          <div>
            <span>
              <Button
                style={{ display: "flex" }}
                variant="contained"
                color="neutral"
                onClick={navToHome}
                className="navHome"
              >Return Home</Button>

              <h1 style={{ display: "flex" }}>
                PlexTech Grader Portal
              </h1>
            </span>

            <div>
              <h4>
                For each applicant, please provide a rating and leave thorough
                comments for every essay question.
              </h4>

              <form onSubmit={formik.handleSubmit}>

                <label htmlFor="timestamp">Timestamp</label>
                <div className="horizontal-box">
                  <p>{typeof userInfo.timestamp != typeof undefined ? userInfo.timestamp : 'N/A'}</p>
                </div>

                <label htmlFor="firstName">First Name</label>
                <div className="horizontal-box">
                  <p>{userInfo.firstName}</p>
                </div>

                <label htmlFor="lastName">Last Name</label>
                <div className="horizontal-box">
                  <p>{userInfo.lastName}</p>
                </div>

                <label htmlFor="graduationYear">Graduation Year</label>
                <div className="horizontal-box">
                  <p>{userInfo.year}</p>
                </div>

                <label htmlFor="major">Major</label>
                <div className="horizontal-box">
                  <p>{userInfo.major}</p>
                </div>

                <label htmlFor="gender">Gender</label>
                <div className="horizontal-box">
                  <p>{userInfo.gender}</p>
                </div>

                <label htmlFor="resume">Resume/CV</label>

                {/* Resume */}
                <div className="horizontal-box">
                  <iframe src={userInfo.resume} />
                  <label htmlFor="comment0">Comment</label>
                  <textarea
                    className="commentHeight"
                    id="comment0"
                    type="text"
                    wrap="soft"
                    onChange={(event) => { setComment0(event.target.value) }}
                  />

                  <label htmlFor="rating0">
                    Overall resume quality:
                  </label>
                  <select className="dropbtn" name="rating0" value={rating1} onChange={(event) => { setRating0(event.target.value) }}>
                    <option value="" disabled={true}>Choose a rating:</option>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                  </select>
                </div>

                {/* Essay Question 1 */}
                <div className="horizontal-box">
                  <label htmlFor="answer1">
                    Long Answer: Why PlexTech?
                  </label>
                  <p>{userInfo.answer1}</p>

                  <label htmlFor="comment1">Comment</label>

                  <textarea
                    className="commentHeight"
                    id="comment1"
                    type="text"
                    wrap="soft"
                    onChange={(event) => { setComment1(event.target.value) }}
                  />

                  <label htmlFor="rating1">
                    Choose a rating for this essay question:
                  </label>
                  <select className="dropbtn" name="rating1" value={rating1} onChange={(event) => { setRating1(event.target.value) }}>
                    <option value="" disabled={true}>Choose a rating:</option>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                  </select>
                </div>

                {/* Essay Question 2 */}
                <div className="horizontal-box">
                  <label htmlFor="essay2">
                    Long Answer: A Story about Yourself
                  </label>
                  <p>{userInfo.answer2}</p>

                  <label htmlFor="comment2">Comment</label>
                  <textarea
                    className="commentHeight"
                    id="comment2"
                    type="text"
                    wrap="soft"
                    onChange={(event) => { setComment2(event.target.value) }}
                  />

                  <label htmlFor="rating2">
                    Choose a rating for this essay question:
                  </label>
                  <select className="dropbtn" name="rating2" value={rating2} onChange={(event) => { setRating2(event.target.value) }}>
                    <option value="" disabled={true}>Choose a rating:</option>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                  </select>
                </div>

                {/* Time Commitments */}
                <div className="horizontal-box">
                  <label htmlFor="commitments">
                    Time Commitments
                  </label>
                  <p>{userInfo.commitments}</p>

                  <label htmlFor="comment3">Comment</label>
                  <textarea
                    className="commentHeight"
                    id="comment3"
                    type="text"
                    wrap="soft"
                    onChange={(event) => { setComment3(event.target.value) }}
                  />

                  <label htmlFor="rating3">
                    Choose a rating for this essay question:
                  </label>
                  <select className="dropbtn" name="rating3" value={rating3} onChange={(event) => { setRating3(event.target.value) }}>
                    <option value="" disabled={true}>Choose a rating:</option>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                    <option value={4}>4</option>
                    <option value={5}>5</option>
                  </select>
                </div>

                {/* Submit Button */}
                <div>
                  <Button
                    type="submit"
                    variant="contained"
                    color="neutral"
                    fontWeight="Bold"
                    style={{
                      width: "12%",
                      marginBottom: "4rem",
                      marginTop: "2.5rem"
                    }}
                  >
                    Submit
                  </Button>
                </div>

              </form>
            </div>
          </div>
        )}
      </Formik>
    </ThemeProvider>
  );
};

export default GraderForm;
