import React from "react";
import { Formik } from "formik";
import * as Yup from 'yup';
import "./styles.css";
import Button from '@mui/material/Button';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from "react-router-dom";

const theme = createTheme({
  status: {
    danger: '#e53e3e',
  },
  palette: {
    primary: {
      main: '#ff8a00',
    //   darker: '#053e85',
    },
    neutral: {
      main: '#ffffff',
       contrastText: '#ff8a00',
    },
  },
});
//This is the code that we took from stack overflow for the text expanding part
var myTextArea = document.getElementsByTagName('textarea')[0];
var myTextLength = myTextArea.value.length
var myTextWidth = parseInt(window.getComputedStyle(myTextArea).width);
var myTextMinLength = 20;
var myTextMaxWidth = ((parseInt(window.getComputedStyle(document.body).width) / 100) * 80);

function checkTextLength() {
    myTextLength = myTextArea.value.length;

    if ((myTextLength > myTextMinLength) && (myTextWidth < (myTextMaxWidth))) {
            myTextWidth += 8;
        }

    myTextArea.style.width = myTextWidth + 'px';
}

myTextArea.addEventListener('keypress', checkTextLength, false);

const InvoiceForm = () => {
    const navigate = useNavigate();

    const navToHome = () => {
      navigate("/");
  }
      return (
        <ThemeProvider theme={theme}>
          <Formik
              initialValues = {{
                timestamp: Date.now(),  
                firstName : 'John',
                lastName : 'Smith',
                resume : 'Resume.pdf',
                essay1 : 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
                essay2 : 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
                essay3 : 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
                essay4 : 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
              }}
              validationSchema = {
                  Yup.object(
                      {
                          firstName: Yup.string().required('Required'),
                          lastName: Yup.string().required('Required'),
                      }
                  )
              }
              onSubmit={ async (values) => {
                  fetch('<ENTER API ENDPOINT HERE>', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                      first_name: values.firstName,
                      last_name: values.lastName,
                      time_created: values.timestamp,
                     })
                  });
              }}
          >
              {formik => (
                    <div>
                      <span>
                        <Button style={{"display": "flex"}}
                        variant = "contained"
                        color = "neutral"
                        onClick={navToHome} 
                        className="navHome"
                        >Return Home</Button>
                        <h1 style={{"display": "flex"}}>
                          PlexTech Member Application - Spring 2023
                        </h1>
                        <br/>
                      </span>    
                      <div>
                        <h4>
                          Thank you for your interest in PlexTech! Please fill out the information below and we will get back to you as soon as possible.
                        </h4>
                        <form onSubmit={formik.handleSubmit}>
                        {/* <label htmlFor="firstName">First Name</label>
                        <input
                          id="firstName"
                          type="text"
                          {...formik.getFieldProps('firstName')}
                        />
                        {formik.touched.firstName && formik.errors.firstName ? <div>{formik.errors.firstName}</div> : null}

                        <label htmlFor="lastName">Last Name</label>
                        <input
                          id="lastName"
                          type="text"
                          {...formik.getFieldProps('lastName')}
                        />
                        {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}
                   */}
                        <label htmlFor="timestamp">Timestamp</label>
                        <input
                          id="timestamp"
                          name="timestamp"
                          type="text"
                          readOnly
                          value={formik.values.timestamp}
                        />

                        <label htmlFor="firstName">First Name</label>
                        <input
                          id="firstName"
                          name="firstName"
                          type="text"
                          readOnly
                          value={formik.values.firstName}
                        />

                        <label htmlFor="lastName">Last Name</label>
                        <input
                          id="lastName"
                          name="lastName"
                          type="text"
                          readOnly
                          value={formik.values.lastName}
                        />

                        <label htmlFor="resume">Resume Upload</label>
                        <input
                          id="resume"
                          name="resume"
                          type="text"
                          readOnly
                          value={formik.values.resume}
                        />

                        {/* Long Answer: Class and EC Commitments',
                essay2 : 'Long Answer: Essay Question 1',
                essay3 : 'Long Answer: Essay Question 2',
                essay4 : 'Long Answer: Essay Question 3'
                         */}

                        <div class='horizontal-box'>
                          <div>
                            <label htmlFor="essay1">Long Answer: Class and EC Commitments</label>
                            <input
                              id="essay1"
                              name="essay1"
                              type="text"
                              readOnly
                              value={formik.values.essay1}
                            />
                          </div>

                          <div>
                          <label htmlFor="comment1">Comment</label>

                              <input
                                id="comment1"
                                type="text"
                                {...formik.getFieldProps('comment1')}
                              />
                              {/* {formik.touched.firstName && formik.errors.firstName ? <div>{formik.errors.firstName}</div> : null} */}

                          </div>



                          </div>

                        <div class='horizontal-box'>

                          <div>
                            <label htmlFor="essay2">Long Answer: Essay Question 1</label>
                            <input
                              id="essay2"
                              name="essay2"
                              type="text"
                              readOnly
                              value={formik.values.essay2}
                            />
                          </div>

                          <div>
                            <label htmlFor="comment2">Comment</label>
                            <input
                              id="comment2"
                              type="text"
                              {...formik.getFieldProps('comment2')}
                            />
                          </div>
                        </div>


                        <div class='horizontal-box'>
                          <div>
                            <label htmlFor="essay3">Long Answer: Essay Question 2</label>
                            <input
                              id="essay3"
                              name="essay3"
                              type="text"
                              readOnly
                              value={formik.values.essay3}
                            />
                          </div>

                          <div>
                            <label htmlFor="comment3">Comment</label>
                            <input
                              id="comment3"
                              type="text"
                              {...formik.getFieldProps('comment3')}
                            />


                          </div>
                        </div>

                        <div class='horizontal-box'>
                          <div>
                            <label htmlFor="essay4">Long Answer: Essay Question 3</label>
                            <input
                              id="essay4"
                              name="essay4"
                              type="text"
                              readOnly
                              value={formik.values.essay4}
                            />
                          </div>

                          <div>

                            <label htmlFor="comment4">Comment</label>
                            <input
                              id="comment4"
                              type="text"
                              {...formik.getFieldProps('comment4')}
                            />
                          </div>
                        </div>

                        <div>
                          <Button type="submit"
                          variant = "contained"
                          color = "neutral"
                          fontweight = "Bold"
                          >Submit</Button>
                        </div>
                      </form>
                      </div>  
                    </div>    
              )}
          </Formik>
        </ThemeProvider>
      );
